import React, {useState} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';


const Registration = () => {


    const [values, setValues] = useState({
        devName : "",
        devEmail : "",
        devPhoto : ""
    })


    const onHandleChange = event =>{
        let name = event.target.name;
        let value = event.target.value;
        console.log(name);
        console.log(value);

        setValues((preValue)=>{
            if(name === "devName"){
                return{
                    devName : value,
                    devEmail : preValue.devEmail,
                    devPhoto : preValue.devPhoto
                }
            }else if(name === "devEmail"){
                return{
                    devName : preValue.devName,
                    devEmail : value,
                    devPhoto : preValue.devPhoto
                }
            }else if(name === "devPhoto"){
                return{
                    devName : preValue.devName,
                    devEmail : preValue.devEmail,
                    devPhoto : value
                }
            }
            console.log(values);

        })

    }


    const onHandleSubmit = (e)=>{
            e.preventDefault();


    }

    return (
        <div>
            <h2 className="text-center mt-4">Enter New Developer</h2>
            <form className="text-center form" method="POST" action="api/register">
                <p>
                    <label id="name" for="empName">Employee Name*</label>
                    <input 
                    onChange={onHandleChange}
                    className=" empName" 
                    type="text" 
                    id="empName" 
                    placeholder="Employee Name" />
                </p>
                <p >
                    <label id="email" for="empMail">Email*</label>
                    <input 
                    onChange={onHandleChange}
                    className="empMail" 
                    type="email" 
                    id="empEmail" 
                    placeholder="Email" />
                </p>
                <p className="profile">
                    <input 
                    onChange={onHandleChange}
                    type="file" 
                    id="empImage" />
                </p>

                <button 
                onClick={onHandleSubmit}
                type="submit" 
                className="btn btn-primary sub">Submit</button>
            </form>
        </div>
    )
}

export default Registration;
