import React from 'react'
import { NavLink } from 'react-router-dom'
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';

export default function NavBar(props) {
     let image = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyddh8D_pxYUXYvoQzcDumtmWZwL04FpLWxsHqlOKkLJRh9A607NtfkI-UjMPLooHR2bM&usqp=CAU";
     let name = "saroj sundara";
     const logout = ()=>{
         if(typeof window !== "undefined"){
             localStorage.removeItem("jwt");
             history.push("/signin")
             axios.get("api/logout")
             .then(res => alert("successfully signed out"))
             .catch(err => console.log(err))
         }
     }
    return (
        <>
        <nav>
            <div>
                <img src={image} alt="profile photo" />
                <h4 className="name">{name}</h4>
            </div>
            <ul>

                <li><NavLink to="/feedback" exact>
                    <button

                        type="submit"
                        className="btn btn-primary"
                    >
                        Add Feedback
                    </button>
                </NavLink></li>
                <li>
                    <button
                        onClick={logout}
                        type="submit"
                        className="btn btn-danger m-4"
                    >
                        Logout
                    </button>
                </li>
            </ul>
        </nav>
        </>
    )
}
