import React, {useState} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';


const Registration = () => {


    const [name, setName] = useState()
    const [email, setEmail] = useState()
    const [image, setImage] = useState()



    const onNameChange = event =>{
        setName(event.target.value)
    }
    const onEmailChange = event =>{
        setEmail(event.target.value)
    }
    const onImageChange = event =>{
        setImage(event.target.value)
    }


    const onHandleSubmit = (e)=>{
            e.preventDefault();

            alert(name)
            alert(email)
            alert(image)


    }

    return (
        <div>
            <h2 className="text-center mt-4">Enter New Developer</h2>
            <form className="text-center form" method="POST" action="api/register">
                <p>
                    <label id="name" for="empName">Employee Name*</label>
                    <input 
                    onChange={onNameChange}
                    className=" empName" 
                    type="text" 
                    id="empName" 
                    name = "devName"
                    placeholder="Employee Name" />
                </p>
                <p >
                    <label id="email" for="empMail">Email*</label>
                    <input 
                    onChange={onEmailChange}
                    className="empMail" 
                    type="email" 
                    id="empEmail"
                    name="devEmail" 
                    placeholder="Email" />
                </p>
                <p className="profile">
                    <input 
                    onChange={onImageChange}
                    type="file" 
                    id="empImage" 
                    name="devImage"
                    />
                </p>

                <button 
                onClick={onHandleSubmit}
                type="submit" 
                className="btn btn-primary sub">Submit</button>
            </form>
        </div>
    )
}

export default Registration;
