import React, {useState} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';


const Registration = () => {


    const [values, setValues] = useState({
        name : "",
        email : "",
        photo : ""
    })

    const {name, email, photo} = values;


    const onHandleChange = name => event =>{
        const value = name === "photo" ? event.target.files[0] : event.target.value;
        setValues({...values, [name] : value})

    }


    const onHandleSubmit = (e)=>{
           e.preventDefault()
           console.log(name);
           console.log(email);
           console.log(photo);

    }

    return (
        <div>
            <h2 className="text-center mt-4">Enter New Developer</h2>
            <form className="text-center form" method="POST" action="api/register">
                <p>
                    <label id="name" for="empName">Employee Name*</label>
                    <input 
                    onChange={onHandleChange}
                    className=" empName" 
                    type="text" 
                    id="empName" 
                    name = "name"
                    placeholder="Employee Name" />
                </p>
                <p >
                    <label id="email" for="empMail">Email*</label>
                    <input 
                    onChange={onHandleChange}
                    className="empMail" 
                    type="email" 
                    id="empEmail"
                    name="email" 
                    placeholder="Email" />
                </p>
                <p className="profile">
                    <input 
                    onChange={onHandleChange}
                    type="file" 
                    id="empImage" 
                    name="photo"
                    />
                </p>

                <button 
                onClick={onHandleSubmit}
                type="submit" 
                className="btn btn-primary sub">Submit</button>
            </form>
        </div>
    )
}

export default Registration;
