import React, {useState} from 'react';
import axios from "axios";

export default function FeedbackCard({data, removeFeedback}) {

  const [feadback, setFeedback] = useState("");
  let name = data.name;
  let image = data.image;
  let email = localStorage.getItem('email');
  const feadbackOb = {
    feadback,
    email
  }

  const onFeedbackChange = (event) => {
    setFeedback(event.target.value);
  };

  const onHandleSubmit = (e)=>{
    e.preventDefault();
    if(isOkay()){
      axios.post('http://localhost:4000/addFeadback', feadbackOb)
      .then(function (response) {
        removeFeedback(name);
        alert("Feedback given successfully")
      })
      .catch(function (error) {
        console.log("FRR",error);
        alert("Unable to submit feedback")
      });
    }
  }

  const isOkay = ()=>{
    let res = false;
    if(feadback.length !== 0){
      res = true;
    }
    return res;
  }

    return (
        <div class="card" style={{height : "60vh"}}>
  <img class="card-img-top fd" style={{marginLeft : "40px"}} src={image} alt="Card image cap" />
  <div class="card-body" style={{marginTop : "190px"}}>
    <h5 class="card-title">{name}</h5>
    <p class="card-text">
        <textarea 
        onChange = {onFeedbackChange}
        name = "feedback"
        placeholder="Write your feedback here..."
        style={{resize : "none", border : "1px solid black"}}>

        </textarea>
        <h5>Max 100 characters</h5>
    </p>
    <button
          onClick={onHandleSubmit}
          type="submit"
          className="btn btn-primary"
        >
          Submit Feedback
        </button>
  </div>
</div>
    )
}
