import React, {useState} from 'react'
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';


const Registration = () => {


    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [image, setImage] = useState("")



    const onNameChange = event =>{
        setName(event.target.value)
    }
    const onEmailChange = event =>{
        setEmail(event.target.value)
    }
    const onImageChange = event =>{
        setImage(event.target.files[0]);
        const formData = new FormData();
        formData.append("image",image)
    }


    const reg = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

    const onHandleSubmit = (e)=>{
            e.preventDefault(); 
            if (name === "" || email === "" || image === ""){
                alert("fields can not be empty!")
            }else if(! (/^[a-zA-Z ]+$/.test(name))){
                alert("please give a valid name!")
                setName("")
            }else if(!reg.test(String(email).toLowerCase())){
                alert("please give a valid email id")
                setEmail("")
            }else if(image.split(".")[1] !== "jpg" || image.split(".")[1] !== "png" 
             || image.split(".")[1] !== "jpeg"){
                  alert("please five only jpeg/jpg/png files")
                  setImage("")
            }else{
                axios.post('api/register', {
                    name,
                    email,
                    image
                  })
                  .then(function (response) {
                    console.log(response);
                  })
                  .catch(function (error) {
                    alert("not able to register user")
                    console.log(error);
                  });
            }


    }

    return (
        <div>
            <h2 className="text-center mt-4">Enter New Developer</h2>
            <form className="text-center form" method="POST" action="api/register">
                <p>
                    <label id="name" for="empName">Employee Name*</label>
                    <input 
                    onChange={onNameChange}
                    className=" empName" 
                    type="text" 
                    id="empName" 
                    name = "devName"
                    placeholder="Employee Name" 
                    value={name}
                    />
                </p>
                <p >
                    <label id="email" for="empMail">Email*</label>
                    <input 
                    onChange={onEmailChange}
                    className="empMail" 
                    type="email" 
                    id="empEmail"
                    name="devEmail" 
                    placeholder="Email"
                    value={email}
                    />
                </p>
                <p className="profile">
                    <input 
                    onChange={onImageChange}
                    type="file" 
                    id="empImage" 
                    name="devImage"
                    accept=".jpg,.jpeg,.png"
                    value={image}
                    />
                </p>

                <button 
                onClick={onHandleSubmit}
                type="submit" 
                className="btn btn-primary sub">Submit</button>
            </form>
        </div>
    )
}

export default Registration;
