import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { Redirect, useHistory} from 'react-router-dom';


const UserSignin = () => {

  let history = useHistory();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const onEmailChange = (event) => {
    setEmail(event.target.value);
  };
  const onPasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const data = {
      email,
      password
  }

  const isOkay = () => {
    let res = true;
    if (email === "" || password === "") {
      alert("fields can not be empty!");
      res = false;
    } else if (!reg.test(String(email).toLowerCase())) {
      alert("please give a valid email id");
      setEmail("");
      res = false;
    }else if(password.length <8){
      alert("minimum eight characters required");
      setPassword("");
      res = false;
    }
    return res;
  };

  const reg =
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  const onHandleSubmit = (e) => {
    e.preventDefault();
    if (isOkay()) {
      localStorage.setItem("userEmail", email);
      axios.post("https://ec33d34c9a52.ngrok.io/DeveloperSignin", data, {
        header : {
          "Content-Type" : "application/json"
        },
        withCredentials: true,
      })
      .then((res)=>{
        console.log("LOGG", res);
          if(res.status === 200){
             if(typeof window !== "undefined"){
               localStorage.setItem("jwtToken", JSON.stringify(res.data.UserLogin.tokens[0]))
               console.log("jwtToken", localStorage.getItem("jwtToken"));
               alert("login successfull")
                history.push("/dashboard")
             } 
          }
      })
      .catch((error)=>{
        alert("ERROR")
          console.log("ERROR", error);
          alert("Can not sign in")
      })
    }
  };

  return (
    <div>
      <h2 className="text-center mt-4">Login</h2>
      <form className="text-center form">
        <p>
          <label id="email" for="empMail">
            Email*
          </label>
          <input
            onChange={onEmailChange}
            className="empMail"
            type="email"
            id="empEmail"
            name="devEmail"
            placeholder="Email"
            value={email}
          />
        </p>
        <p className="profile">
        <label id="password" for="empPassword">
            Password*
          </label>
          <input
            onChange={onPasswordChange}
            type="password"
            id="empPassword"
            name="devPassword"
            placeholder="Password"
            value={password}
          />
        </p>

        <button
          onClick={onHandleSubmit}
          type="submit"
          className="btn btn-primary sub"
        >
          Login
        </button>
      </form>
    </div>
  );
};

export default UserSignin;
