import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';


const Registration = () => {

    const isCorrectData = (e)=>{
            e.preventDefault();
            let name = document.getElementById("name").value;
            let email = document.getElementById("email").value;
            let image = document.getElementById("empImage").value;
            alert(name.length)
            if(name.length === 0 || email.length === 0 || image.length === 0){
                alert("Fields can not be empty!")
                return false;
            }
    }

    return (
        <div>
            <h2 className="text-center mt-4">Enter New Developer</h2>
            <form className="text-center form" onSubmit={isCorrectData} method="POST" action="api/register">
                <p>
                    <label id="name" for="empName">Employee Name*</label>
                    <input className=" empName" type="text" id="empName" placeholder="Employee Name" />
                </p>
                <p >
                    <label id="email" for="empMail">Email*</label>
                    <input className="empMail" type="email" id="empEmail" placeholder="Email" />
                </p>
                <p className="profile">
                    <input type="file" id="empImage" />
                </p>

                <button type="submit" className="btn btn-primary sub">Submit</button>
            </form>
        </div>
    )
}

export default Registration;
