import React, { useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { Redirect} from 'react-router-dom';


const Registration = () => {

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  let [profileImage, setImage] = useState(null);

  const onNameChange = (event) => {
    setName(event.target.value);
  };
  const onEmailChange = (event) => {
    setEmail(event.target.value);
  };
  const onImageChange = (event) => {
    // console.log("EVENT",event.target);
    setImage(event.target.files[0]);
  };

  //  console.log("IMAGE STATE",image);

  const userOb = {
      name,
      email,
      profileImage
  }

  const isOkay = () => {
    let res = true;
    if (name === "" || email === "" || profileImage === "") {
      alert("fields can not be empty!");
      res = false;
    } else if (!/^[a-zA-Z ]+$/.test(name)) {
      alert("please give a valid name!");
      setName("");
      res = false;
    } else if (!reg.test(String(email).toLowerCase())) {
      alert("please give a valid email id");
      setEmail("");
      res = false;
    }
    return res;
  };

  const reg =
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  const onHandleSubmit = (e) => {
    e.preventDefault();
    if (isOkay()) {
      const data = new FormData();
      data.append("data", userOb);
      axios.post("https://8b1d832ec85e.ngrok.io/register", data, {
        headers : {
          Accept : "application/json",
          "Content-Type": "application/json",
      }
      })
      .then(res => {
        console.log("RESPONSE", res);
        if(res.status === 200){
          return <Redirect to='/signin' />
       }

      })
      // .then(data =>{
      //     console.log("DATA", data);

      // })
      // .catch((error)=>{
      //     console.log("ERROR",error);
      //     alert("Can not register")
      // })
    }
  };

  return (
    <div>
      <h2 className="text-center mt-4">Enter New Developer</h2>
      <form className="text-center form" method="POST" action="api/register">
        <p>
          <label id="name" for="empName">
            Employee Name*
          </label>
          <input
            onChange={onNameChange}
            className=" empName"
            type="text"
            id="empName"
            name="devName"
            placeholder="Employee Name"
            value={name}
          />
        </p>
        <p>
          <label id="email" for="empMail">
            Email*
          </label>
          <input
            onChange={onEmailChange}
            className="empMail"
            type="email"
            id="empEmail"
            name="devEmail"
            placeholder="Email"
            value={email}
          />
        </p>
        <p className="profile">
          <input
            onChange={onImageChange}
            type="file"
            // id="empImage"
            // name="devImage"
             accept=".jpg,.jpeg,.png"
            // value={image}
          />
        </p>

        <button
          onClick={onHandleSubmit}
          type="submit"
          className="btn btn-primary sub"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default Registration;
