import axios from 'axios';
import React, {useEffect, useState} from 'react'
import NavBar from './NavBar';
import { Container, Row, Col } from 'reactstrap';
import FeedbackCard from './FeedbackCard';
import { useHistory } from 'react-router-dom';

export default function AddFeedback(props) {
    let token = null;
    let history = useHistory();

    const [data, setData] = useState([])

    const removeFeedback = (name)=>{
        setData(data.filter(eachFeedback => eachFeedback.name !== name))
    }

    const fetchDetails = async()=>{
        const {data} = await axios.get("https://9c52279df4d8.ngrok.io/addFeadback", {
            headers : {
                Accept : "application/json",
                "Content-Type": "application/json",
                // Authorization : `Bearer ${token}`
            }
        })
         const allFeedbacks = data.map(d =>({
             image : d.feedback,
             name: d.time,
         }))

         setData(allFeedbacks);
    }


    useEffect(()=>{
        if(typeof window === "undefined")
            history.push("/signin")
        else if(localStorage.getItem("jwt")){
            token = localStorage.getItem("jwt").token;
            fetchDetails();
        }
        else
            history.push("/signin")

    }, []);

    return (
        <div>
            <NavBar />
            <div className="cards container">
                <Container fluid>
                    <Row>
                        {
                            data.map((d, index) => (
                                <Col md={4} key={index}>
                                    <FeedbackCard data={d} removeFeedback={removeFeedback}/>
                                </Col>
                            ))
                        }
                    </Row>  
                </Container>

            </div>
        </div>
    )
}
